import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import {
  FaUserCircle,
  FaCheckCircle,
  FaTimes,
  FaShareAlt,
} from "react-icons/fa";

function App() {
  const [photos, setPhotos] = useState([]);
  const [shareModal, setShareModal] = useState(false);

  useEffect(() => {
    fetch("http://localhost:3001/photos")
      .then((res) => res.json())
      .then((data) => {
        let unique = [
          ...new Map(
            data.photos.map((photo) => [photo["url"], photo])
          ).values(),
        ];
        unique.map((photo) => (photo["selected"] = false));
        setPhotos(unique);
      });
  }, []);

  const handleSelect = (id) => {
    let updatedPhotos = [...photos];
    let index = updatedPhotos.findIndex((photo) => photo.id === id);
    updatedPhotos[index].selected = !updatedPhotos[index].selected;
    setPhotos(updatedPhotos);
  };

  const handleClear = () => {
    let updatedPhotos = [...photos];
    updatedPhotos.map((photo) => (photo.selected = false));
    setPhotos(updatedPhotos);
    setShareModal(false);
  };

  const handleToggleShareModal = () => {
    setShareModal(!shareModal);
  };

  const handleCancel = () => {
    setShareModal(false);
  };

  const handleShare = () => {
    let selectedIDs = photos.reduce((ids, photo) => {
      if (photo.selected) {
        ids.push(photo.id);
      }
      return ids;
    }, []);

    fetch(`http://localhost:3001/website/photos/${selectedIDs[0]}`, {
      method: "POST",
    })
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
      });
    //handleClear();
  };

  return (
    <div className="App">
      {photos && photos.length > 0 && (
        <div className="photoGrid">
          {photos.map((photo) => (
            <Photo
              key={photo.id}
              url={photo.url}
              selected={photo.selected}
              onSelect={() => {
                handleSelect(photo.id);
              }}
            />
          ))}
        </div>
      )}
      {shareModal && (
        <div className="shareModal">
          Send {photos.filter((photo) => !!photo.selected).length} photos to
          website?
          <div className="modalButtons">
            <button className="button cancelButton" onClick={handleCancel}>
              Cancel
            </button>
            <button className="button shareButton" onClick={handleShare}>
              Send to Website
            </button>
          </div>
          <div className="triangle"></div>
        </div>
      )}
      <Footer
        count={photos.filter((photo) => !!photo.selected).length}
        clearSelection={handleClear}
        toggleShareModal={handleToggleShareModal}
      />
    </div>
  );
}

const Photo = ({ url, selected, onSelect }) => {
  return (
    <div className="photoContainer" onClick={onSelect}>
      <img className="photo" src={url} />
      <FaCheckCircle
        className="check"
        color={selected ? "#1a73e8" : "#d3e6f7"}
        size={30}
      />
    </div>
  );
};

Photo.propTypes = {
  url: PropTypes.string.isRequired,
  selected: PropTypes.bool.isRequired,
  onSelect: PropTypes.func.isRequired,
};

const Footer = ({ count, clearSelection, toggleShareModal }) => {
  return (
    <div className="footerBar">
      {count > 0 && (
        <div className="footerSection left">
          <FaTimes
            className="pointer"
            color="rgb(120,136,150)"
            size={30}
            onClick={clearSelection}
          />
          <div>{count} Selected</div>
        </div>
      )}
      <div className="footerSection right">
        {count > 0 && (
          <FaShareAlt
            className="pointer"
            color="rgb(120,136,150)"
            size={30}
            onClick={toggleShareModal}
          />
        )}
        <FaUserCircle color="rgb(120,136,150)" size={30} />
      </div>
    </div>
  );
};

Footer.propTypes = {
  count: PropTypes.number.isRequired,
  clearSelection: PropTypes.func.isRequired,
  toggleShareModal: PropTypes.func.isRequired,
};

export default App;
