import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import {
  FaUserCircle,
  FaCheckCircle,
  FaTimes,
  FaShareAlt,
} from "react-icons/fa";

function App() {
  const [photos, setPhotos] = useState([]);
  const [selectedPhotos, setSelectedPhotos] = useState([]);
  const [count, setCount] = useState(0);

  useEffect(() => {
    fetch("http://localhost:3001/photos")
      .then((res) => res.json())
      .then((data) => {
        let unique = [
          ...new Map(
            data.photos.map((photo) => [photo["url"], photo])
          ).values(),
        ];
        setPhotos(unique);
      });
  }, []);

  const handleSelect = (id) => {
    let updatedList = selectedPhotos;
    if (updatedList.includes(id)) {
      updatedList = updatedList.filter((item) => item !== id);
      setSelectedPhotos(updatedList);
      setCount(count - 1);
    } else {
      updatedList.push(id);
      setSelectedPhotos(updatedList);
      setCount(count + 1);
    }
  };

  const handleClear = () => {
    setSelectedPhotos([]);
  };

  return (
    <div className="App">
      {photos && photos.length > 0 && (
        <div className="photoGrid">
          {photos.map((photo) => (
            <Photo
              key={photo.id}
              url={photo.url}
              onSelect={() => {
                handleSelect(photo.id);
              }}
            />
          ))}
        </div>
      )}
      {console.log(selectedPhotos)}
      <Footer count={count} clearSelection={handleClear} />
    </div>
  );
}

const Photo = ({ url, onSelect }) => {
  const [selected, setSelected] = useState(false);
  const handleSelect = () => {
    setSelected(!selected);
    onSelect();
  };

  return (
    <div className="photoContainer" onClick={handleSelect}>
      <img className="photo" src={url} />
      <FaCheckCircle
        className="check"
        color={selected ? "#1a73e8" : "#d3e6f7"}
        size={30}
      />
    </div>
  );
};

Photo.propTypes = {
  url: PropTypes.string.isRequired,
  onSelect: PropTypes.func.isRequired,
};

const Footer = ({ count, clearSelection }) => {
  return (
    <div className="footerBar">
      {count > 0 && (
        <div className="footerSection left">
          <FaTimes
            className="pointer"
            color="rgb(120,136,150)"
            size={30}
            onClick={clearSelection}
          />
          <div>{count} Selected</div>
        </div>
      )}
      <div className="footerSection right">
        {count > 0 &&
          <FaShareAlt
            className="pointer"
            color="rgb(120,136,150)"
            size={30}
          />
        }
        <FaUserCircle color="rgb(120,136,150)" size={30} />
      </div>
    </div>
  );
};

Footer.propTypes = {
  count: PropTypes.number.isRequired,
  clearSelection: PropTypes.func.isRequired,
};

export default App;
