import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import { FaUserCircle, FaCheckCircle } from "react-icons/fa";

function App() {
  const [photos, setPhotos] = useState([]);
  const [selectedPhotos, setSelectedPhotos] = useState([]);

  useEffect(() => {
    fetch("http://localhost:3001/photos")
      .then((res) => res.json())
      .then((data) => {
        let unique = [
          ...new Map(
            data.photos.map((photo) => [photo["url"], photo])
          ).values(),
        ];
        setPhotos(unique);
      });
  }, []);

  const handleSelect = (id) => {
    console.log(selectedPhotos);
    let updatedList = selectedPhotos;
    if (updatedList.includes(id)) {
      console.log(updatedList);
      updatedList = updatedList.filter((item) => item !== id);
      console.log(updatedList);
      setSelectedPhotos(updatedList);
      console.log(selectedPhotos);
    } else {
      console.log(updatedList);
      updatedList = selectedPhotos;
      updatedList.push(id);
      console.log(updatedList);
      setSelectedPhotos(updatedList, () => console.log(selectedPhotos));
    }
  };

  return (
    <div className="App">
      {photos && photos.length > 0 && (
        <div className="photoGrid">
          {photos.map((photo) => (
            <Photo
              key={photo.id}
              url={photo.url}
              onSelect={() => {
                handleSelect(photo.id);
              }}
            />
          ))}
        </div>
      )}
      <Footer count={selectedPhotos.length} />
    </div>
  );
}

const Photo = ({ url, onSelect }) => {
  const [selected, setSelected] = useState(false);
  const handleSelect = () => {
    setSelected(!selected);
    onSelect();
  };

  return (
    <div className="photoContainer" onClick={handleSelect}>
      <img className="photo" src={url} />
      <FaCheckCircle
        className="check"
        color={selected ? "#1a73e8" : "#d3e6f7"}
        size={30}
      />
    </div>
  );
};

Photo.propTypes = {
  url: PropTypes.string.isRequired,
  onSelect: PropTypes.func.isRequired,
};

const Footer = ({ count }) => {
  return (
    <div className="footerBar">
      <div>{count}</div>
      <FaUserCircle className="userIcon" color="rgb(120,136,150)" size={30} />
    </div>
  );
};

Footer.propTypes = {
  count: PropTypes.number.isRequired,
};

export default App;
