import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import { FaUserCircle, FaCheckCircle } from "react-icons/fa";

function App() {
  const [photos, setPhotos] = useState([]);

  useEffect(() => {
    fetch("http://localhost:3001/photos")
      .then((res) => res.json())
      .then((data) => {
        let photos = data.photos;
        let unique = [
          ...new Map(photos.map((item) => [item["url"], item])).values(),
        ];
        setPhotos(unique);
      });
  }, []);

  return (
    <div className="App">
      {photos && photos.length > 0 && (
        <div className="photoGrid">
          {photos.map((photo) => (
            <Photo key={photo.id} url={photo.url} selected={false} />
          ))}
        </div>
      )}
      <Footer />
    </div>
  );
}

const Photo = ({ url, selected }) => {
  const handleClick = () => {
    console.log(selected);
    selected = !selected;
    console.log(selected);
  };

  return (
    <div className="photoContainer" onClick={handleClick}>
      <img className="photo" src={url} />
      <FaCheckCircle
        className="check"
        color={selected ? "#1a73e8" : "#d3e6f7"}
        size={30}
      />
    </div>
  );
};

Photo.propTypes = {
  url: PropTypes.string.isRequired,
  selected: PropTypes.bool.isRequired,
};

const Footer = () => {
  return (
    <div className="footerBar">
      <FaUserCircle className="userIcon" color="rgb(120,136,150)" size={30} />
    </div>
  );
};

export default App;
