import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import { FaUserCircle, FaCheckCircle } from "react-icons/fa";

function App() {
  const [photos, setPhotos] = useState([]);

  useEffect(() => {
    fetch("http://localhost:3001/photos")
      .then((res) => res.json())
      .then((data) => {
        let unique = [
          ...new Map(data.photos.map((photo) => [photo["url"], photo])).values(),
        ];
        return unique.map((photo) => {
          photo["selected"] = false;
        });
      })
      .then((unique) => {
        setPhotos(unique);
      });
  }, []);

  const handleSelect = (id) => {
    let index = photos.findIndex((photo) => photo.id === id);
    photos[index].selected = !photos[index].selected;
    setPhotos(photos);
  };

  return (
    <div className="App">
      {photos && photos.length > 0 && (
        <div className="photoGrid">
          {photos.map((photo) => (
            <Photo
              key={photo.id}
              url={photo.url}
              selected={photo.selected}
              onSelect={handleSelect(photo.id)}
            />
          ))}
        </div>
      )}
      <Footer count={1} />
    </div>
  );
}

const Photo = ({ url, selected, onSelect }) => {
  //const [selected, setSelected] = useState(false);
  // const handleSelect = () => {
  //   setSelected(!selected);
  //   onSelect();
  // };

  return (
    <div className="photoContainer" onClick={onSelect}>
      <img className="photo" src={url} />
      <FaCheckCircle
        className="check"
        color={selected ? "#1a73e8" : "#d3e6f7"}
        size={30}
      />
    </div>
  );
};

Photo.propTypes = {
  url: PropTypes.string.isRequired,
  selected: PropTypes.bool.isRequired,
  onSelect: PropTypes.func.isRequired,
};

const Footer = ({ count }) => {
  return (
    <div className="footerBar">
      <div>{count}</div>
      <FaUserCircle className="userIcon" color="rgb(120,136,150)" size={30} />
    </div>
  );
};

Footer.propTypes = {
  count: PropTypes.number.isRequired,
};

export default App;
